def create_spoken_forms(source: str, words_to_exclude: Optional[list[str]]=None, minimum_term_length: int=DEFAULT_MINIMUM_TERM_LENGTH, generate_subsequences: bool=True) -> list[str]:
    """Create spoken forms for a given source"""
    if words_to_exclude is None:
        words_to_exclude = []
    spoken_forms_without_symbols = create_spoken_forms_from_regex(source, REGEX_NO_SYMBOLS)
    spoken_forms_with_symbols = create_spoken_forms_from_regex(source, REGEX_WITH_SYMBOLS)
    full_spoken_forms = list(set(spoken_forms_with_symbols + spoken_forms_without_symbols))
    if generate_subsequences:
        terms = generate_string_subsequences(spoken_forms_without_symbols[-1], words_to_exclude, minimum_term_length)
        for form in full_spoken_forms:
            if form not in terms:
                terms.append(form)
    else:
        terms = full_spoken_forms
    return terms

def create_spoken_forms_from_list(sources: list[str], words_to_exclude: Optional[list[str]]=None, minimum_term_length: int=DEFAULT_MINIMUM_TERM_LENGTH, generate_subsequences: bool=True) -> dict[str, str]:
    """Create spoken forms for all sources in a list, doing conflict resolution"""
    return actions.user.create_spoken_forms_from_map({source: source for source in sources}, words_to_exclude, minimum_term_length, generate_subsequences)

def create_spoken_forms_from_map(sources: Mapping[str, Any], words_to_exclude: Optional[list[str]]=None, minimum_term_length: int=DEFAULT_MINIMUM_TERM_LENGTH, generate_subsequences: bool=True) -> dict[str, Any]:
    """Create spoken forms for all sources in a map, doing conflict resolution"""
    all_spoken_forms: defaultdict[str, list[SpeakableItem]] = defaultdict(list)
    for (name, value) in sources.items():
        spoken_forms = actions.user.create_spoken_forms(name, words_to_exclude, minimum_term_length, generate_subsequences)
        for spoken_form in spoken_forms:
            all_spoken_forms[spoken_form].append(SpeakableItem(name, value))
    final_spoken_forms = {}
    for (spoken_form, spoken_form_sources) in all_spoken_forms.items():
        if len(spoken_form_sources) > 1:
            final_spoken_forms[spoken_form] = min(spoken_form_sources, key=lambda speakable_item: len(speakable_item.name)).value
        else:
            final_spoken_forms[spoken_form] = spoken_form_sources[0].value
    return final_spoken_forms

